import React, { useState } from 'react';


function Todo(){
const [name,setName]=useState("")
    function add(event){
        var name1=event.target.value;
        setName(name1)

    }
     function listItem(){
        document.write(<li>{name}</li>)
    }
    return (
    <>
    <h1>Todo List</h1>
    <h2>{name}</h2>
    <input type="text"  placeholder="Enter Task" 
    value={name} 
    className="input" 
    onChange={add}
    />
    <button type="submit" onClick={listItem}></button>
    </>
    );
}


export default Todo;
