import React, { useState } from 'react';
import './todo.css';


function Todo(){

const [name,setName]=useState({
    fname:"",
    lname:"",
    email:"",
    phone:"",
});

function add(event){
        // console.log(event.target.value);
     const{name,value}=event.target;
      setName((preValue)=>{
        return {
            ...preValue,
            [name]:value,
        }
         }
          )

    }
    const listItem=(e)=>{
        e.preventDefault();
        alert("form submitted");
        console.log(name)
    }

    return (
    <>
    <h1 className="heading" >Todo List</h1>
    <form className="form_head" onSubmit={listItem}>
    <input type="text"  placeholder="Enter Name" 
    value={name.fname} 
    name="fname"
    className="input" 
    onChange={add}
    required="required"
    />
    <input type="text"  placeholder="Enter Last Name" 
    value={name.lname} 
    name="lname"
    className="input" 
    onChange={add}
    required="required"
    />
     <input type="email"  placeholder="Enter Email" 
    value={name.email} 
    name="fname"
    className="input" 
    onChange={add}
    required="required"
    />
    <input type="number"  placeholder="Enter phone" 
    value={name.phone} 
    name="fname"
    className="input" 
    onChange={add}
    required="required"
    />
    <input type="number"  placeholder="Enter phone" 
    value={name.phone} 
    name="fname"
    className="input" 
    onChange={add}
    required="required"
    />
    <button type="submit" >Add Task</button>
    </form>
    </>

    )
}



export default Todo;
