    value={name.lname} 
    name="lname"
    className="input" 
    onChange={add}
    required="regured"
    />
     <li>{sname.sfname}</li>
     <li>{sname.slname}</li>
    <button type="submit" >Add Task</button>
    </form>
    </>

    );
}


export default Todo;
